package com.example.hashilink.ui.buyer

import android.os.Bundle
import android.view.LayoutInflater
import android.view.View
import android.view.ViewGroup
import android.widget.TextView
import androidx.fragment.app.Fragment
import androidx.recyclerview.widget.LinearLayoutManager
import androidx.recyclerview.widget.RecyclerView
import com.example.hashilink.R
import com.google.firebase.auth.FirebaseAuth
import com.google.firebase.database.*

class ChatHistoryFragment : Fragment() {

    private lateinit var recyclerView: RecyclerView
    private lateinit var adapter: ChatHistoryAdapter
    private val chatList = mutableListOf<String>()

    override fun onCreateView(
        inflater: LayoutInflater, container: ViewGroup?,
        savedInstanceState: Bundle?
    ): View {
        return inflater.inflate(R.layout.fragment_chat_history, container, false)
    }

    override fun onViewCreated(view: View, savedInstanceState: Bundle?) {
        super.onViewCreated(view, savedInstanceState)

        recyclerView = view.findViewById(R.id.rvChatHistory)
        recyclerView.layoutManager = LinearLayoutManager(requireContext())
        adapter = ChatHistoryAdapter(chatList) { sellerId ->
            // Open chat with seller
            val chatFragment = ChatFragment.newInstance(sellerId)
            (parentFragment as? BuyerHomeFragment)?.loadFragment(chatFragment)
        }
        recyclerView.adapter = adapter

        loadChatHistory()
    }

    private fun loadChatHistory() {
        val currentUser = FirebaseAuth.getInstance().currentUser ?: return
        val buyerId = currentUser.uid
        val chatsRef = FirebaseDatabase.getInstance().getReference("chats").child(buyerId)

        chatsRef.addValueEventListener(object : ValueEventListener {
            override fun onDataChange(snapshot: DataSnapshot) {
                chatList.clear()
                for (child in snapshot.children) {
                    chatList.add(child.key!!) // sellerId
                }
                adapter.notifyDataSetChanged()
            }

            override fun onCancelled(error: DatabaseError) {
                // Handle error
            }
        })
    }

    inner class ChatHistoryAdapter(
        private val chats: List<String>,
        private val onItemClick: (String) -> Unit
    ) : RecyclerView.Adapter<ChatHistoryAdapter.ViewHolder>() {

        inner class ViewHolder(itemView: View) : RecyclerView.ViewHolder(itemView) {
            val tvSellerId: TextView = itemView.findViewById(R.id.tvSellerId)
        }

        override fun onCreateViewHolder(parent: ViewGroup, viewType: Int): ViewHolder {
            val view = LayoutInflater.from(parent.context).inflate(R.layout.item_chat_history, parent, false)
            return ViewHolder(view)
        }

        override fun onBindViewHolder(holder: ViewHolder, position: Int) {
            val sellerId = chats[position]
            holder.tvSellerId.text = "Chat with Seller: $sellerId"
            holder.itemView.setOnClickListener { onItemClick(sellerId) }
        }

        override fun getItemCount() = chats.size
    }
}

